mapper.select:
select i_code, i_name, i_mount, i_class, i_weight
from item_list
where i_code=#{i_code}

select i_code, i_name, i_mount, i_class, i_weight
from item_list
where i_name=#{i_name}

select i_code, i_name, i_mount, i_class, i_weight
from item_list
where i_class=#{i_class}

select i_code
from item_list
where i_code=#{findding_code}

select i_code
from item_list
where i_name like #{findding}

select i_code
from item_list
where i_class like #{findding}

mapper.update:
UPDATE item_list
SET i_name=#{i_name}, i_mount=#{i_mount}, i_weight=#{i_weight}, i_class=#{i_class}
where i_code=#{i_code}
